mapper.insert:
INSERT INTO swim_member ( name
                         , user_name
                         , password
                         , email
                         , phone_number
                         )
VALUES ( #{name}
       , #{userName}
       , #{password}
       , #{email}
       , #{phoneNumber}
       )

mapper.update:
UPDATE swim_member SET
        name = #{name}
        , user_name = #{userName}
        , password = #{password}
        , email = #{email}
        , phone_number = #{phoneNumber}
        , role = #{role}
        , max_lock = #{maxLock}
WHERE id = #{id}
