mapper.insert:
INSERT INTO swimpool_tbl (
    name
    , ranes
    , rane_type
    , phone_number
    , addr
) VALUES (
    #{name}
    , #{ranes}
    , #{raneType}
    , #{phoneNumber}
    , #{addr}
)

mapper.update:
UPDATE swimpool_tbl SET
         name = #{name}
        , ranes = #{ranes}
        , rane_type = #{raneType}
        , phone_number = #{phoneNumber}
        , addr = #{addr}
WHERE id = #{id}
